var counter=document.getElementById("counter");document.getElementById("counter_btn").addEventListener("click",function(){for(var e,t=document.getElementsByTagName("input"),n=0;n<t.length;n++)e=t[n].value;if(e>0){var o=1e3*e;setTimeout(function(){alert("tiempo acabdo")},o);var r=setInterval(function(){e>0?(counter.textContent=e,e--):clearInterval(r),console.log(e)},1e3)}else alert("inserte un numero")});